import React, { useEffect, useState } from 'react';
import PropTypes from 'prop-types';


function colorRandom(){
    const COLORLIST = ['green','red','blue','yellow'];

    const random = Math.trunc(Math.random() * 4);
    const colorFinal = COLORLIST[random]; 
    console.log(color);
    return colorFinal;
}

function useMagicColor(props) {
    const [color, setColor] = useState('transparent');
    useEffect(() => {
        const timeOutColor = setTimeout(() =>{
            const newColor = colorRandom();
            setColor(newColor);

        },1000)
        return () => {
            clearTimeout(timeOutColor);
        };
    }, []);

    return {color};
}

export default useMagicColor;
